import { React, useState } from "react";
import { Link } from "react-router-dom";
import Alert from "@mui/material/Alert";

const Register = ({ handleSubmit }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [alertMsg, setAlertMsg] = useState("");

  const displayAlert = () => {
    if (alertMsg === "") return "";
    if (!alertMsg.includes("succesfully")) {
      return <Alert severity="error">{alertMsg}</Alert>;
    } else {
      return <Alert severity="success">{alertMsg}</Alert>;
    }
  };

  return (
    <div>
      {displayAlert()}
      <article className="br3 ba dark-gray b--black-10 mv4 w-100 w-50-m w-25-l mw6 center shadow-5">
        <main className="insideForm pa4 black-80">
          <div className=" measure ">
            <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
              <legend className="f4 fw6 ph0 mh0 ">Create Account</legend>
              <div className="mt3">
                <label className="db fw6 lh-copy f6" htmlFor="email-address">
                  Name
                </label>
                <input
                  className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
                  type="text"
                  name="Name"
                  id="Name"
                  onChange={(event) => setName(event.target.value)}
                />
              </div>

              <div className="mt3">
                <label className="db fw6 lh-copy f6" htmlFor="email-address">
                  Email
                </label>
                <input
                  className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
                  type="email"
                  name="email-address"
                  id="email-address"
                  onChange={(event) => setEmail(event.target.value)}
                />
              </div>
              <div className="mv3">
                <label className="db fw6 lh-copy f6" htmlFor="password">
                  Password
                </label>
                <input
                  className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
                  type="password"
                  name="password"
                  id="password"
                  onChange={(event) => setPassword(event.target.value)}
                />
              </div>
            </fieldset>
            <div className="">
              <input
                className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib"
                type="submit"
                value="Register"
                onClick={() => handleSubmit(email, password, name, setAlertMsg)}
              />
            </div>
            <div className="lh-copy mt3">
              <Link to="/login">Sign in</Link>
            </div>
          </div>
        </main>
      </article>
    </div>
  );
};

export default Register;
