import * as React from "react";
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import Menu from "@mui/material/Menu";
import MenuIcon from "@mui/icons-material/Menu";
import Container from "@mui/material/Container";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import Tooltip from "@mui/material/Tooltip";
import MenuItem from "@mui/material/MenuItem";
import AdbIcon from "@mui/icons-material/Adb";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { setClickedProfile } from "../features/feedSlice";
import Badge from '@mui/material/Badge';
import { useState } from 'react';
import MailIcon from '@mui/icons-material/Mail';
import CircleNotificationsIcon from '@mui/icons-material/CircleNotifications';
import {
  Popover, List, ListItem,
} from '@mui/material';




const pages = ["Feed", "Chats"];
const settings = ["Profile", "Settings", "Logout"];

const Navbar = ({ handleLogOut }) => {

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [anchorElNav, setAnchorElNav] = useState(null);
  const [anchorElUser, setAnchorElUser] = useState(null);
  const [anchorElNot, setAnchorElNot] = useState(null);
  const [anchorElNotUser, setAnchorElNotUser] = useState(null);

  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
  const userOb = useSelector((state) => state.auth.userOb);


  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };
  const handleOpenUserMenu = (event) => {
    // good one
    setAnchorElUser(event.currentTarget);
  };

  const handleOpenNotifications = (event) => {
    setAnchorElNot(event.currentTarget);
  };

  const unSeenNotifications = () => {
    let count = 0;
    userOb.notifications?.forEach((notification) => {
      if (!notification.seen) {
        count++;
      }
    })
    return count;
  }

  const handleCloseNavMenu = (page) => {
    switch (page) {
      case "Feed":
        navigate("/");
        break;
      case "Chats":
        navigate("/chats");
        break;
      case "Profile":
        dispatch(setClickedProfile(null));
        navigate("/myprofile");
        break;
      case "Settings":
        navigate("/settings");
        break;
      case "Logout":
        handleLogOut();
        navigate("/login");
        break;
    }
    setAnchorElNav(null);
    setAnchorElUser(null);
  };

  const handleCloseNotifications = (notification) => {
    console.log(notification)
    setAnchorElNot(null);
    setAnchorElNotUser(null);
  };

  const handleCloseUserNot = (notification) => {
    setAnchorElNotUser(null);
  };


  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };

  return (
    <div>
      <AppBar position="static">
        <Container maxWidth="100%">
          <Toolbar disableGutters>
            <AdbIcon sx={{ display: { xs: "none", md: "flex" }, mr: 1 }} />
            <Typography
              variant="h6"
              noWrap
              component="a"
              href="/"
              sx={{
                mr: 2,
                display: { xs: "none", md: "flex" },
                fontFamily: "monospace",
                fontWeight: 700,
                letterSpacing: ".3rem",
                color: "inherit",
                textDecoration: "none",
              }}
            >
              LOGO
            </Typography>

            <Box sx={{ flexGrow: 1, display: { xs: "flex", md: "none" } }}>
              <IconButton
                size="large"
                aria-label="account of current user"
                aria-controls="menu-appbar"
                aria-haspopup="true"
                onClick={handleOpenNavMenu}
                color="inherit"
              >
                <MenuIcon />
              </IconButton>
              <Menu
                id="menu-appbar"
                anchorEl={anchorElNav}
                anchorOrigin={{
                  vertical: "bottom",
                  horizontal: "left",
                }}
                keepMounted
                transformOrigin={{
                  vertical: "top",
                  horizontal: "left",
                }}
                open={Boolean(anchorElNav)}
                onClose={handleCloseNavMenu}
                sx={{
                  display: { xs: "block", md: "none" },
                }}
              >
                {pages.map((page) => (
                  <MenuItem key={page} onClick={handleCloseNavMenu}>
                    <Typography textAlign="center">{page}</Typography>
                  </MenuItem>
                ))}
              </Menu>
            </Box>
            <AdbIcon sx={{ display: { xs: "flex", md: "none" }, mr: 1 }} />
            <Typography
              variant="h5"
              noWrap
              component="a"
              href="/"
              sx={{
                mr: 2,
                display: { xs: "flex", md: "none" },
                flexGrow: 1,
                fontFamily: "monospace",
                fontWeight: 700,
                letterSpacing: ".3rem",
                color: "inherit",
                textDecoration: "none",
              }}
            >
              LOGO
            </Typography>
            <Box sx={{ flexGrow: 1, display: { xs: "none", md: "flex" } }}>
              {pages.map((page) => (
                <Button
                  key={page}
                  onClick={() => handleCloseNavMenu(page)}
                  sx={{ my: 2, color: "white", display: "block" }}
                >
                  {page}
                </Button>
              ))}
            </Box>



            <Box sx={{ flexGrow: 0 }}>
  <Tooltip title="Open settings">
    {isLoggedIn ? (
      <>
        <IconButton onClick={handleOpenNotifications}>
          <Badge className="mr3" color="secondary" badgeContent={unSeenNotifications() || 0}>
            <CircleNotificationsIcon />
          </Badge>
        </IconButton>

        <Popover
          sx={{ mt: "45px" }}
          id="notification-popover"
          anchorEl={anchorElNot}
          anchorOrigin={{
            vertical: "top",
            horizontal: "right",
          }}
          keepMounted
          transformOrigin={{
            vertical: "top",
            horizontal: "right",
          }}
          open={Boolean(anchorElNot)}
          onClose={handleCloseNotifications}
        >
          <List>
            {userOb.notifications?.map((notification) => (
              <ListItem 
                key={notification._id} 
                onClick={() => handleCloseNotifications(notification)}

              >
                { console.log(notification)}
                <Avatar>
                  <img alt={notification.userId.avatar} src={notification.userId.avatar} />
                </Avatar>
                <Typography sx={{ ml: 2 }} textAlign="center">
                  {notification.userId.name + notification.text}
                </Typography>
              </ListItem>
            ))}
          </List>
        </Popover>

        <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
          <Avatar>
            <img alt={userOb.name} src={userOb.avatar} />
          </Avatar>
        </IconButton>
      </>
    ) : null
    }
  </Tooltip>
              <Menu
                sx={{ mt: "45px" }}
                id="menu-appbar"
                anchorEl={anchorElUser}
                anchorOrigin={{
                  vertical: "top",
                  horizontal: "right",
                }}
                keepMounted
                transformOrigin={{
                  vertical: "top",
                  horizontal: "right",
                }}
                open={Boolean(anchorElUser)}
                onClose={handleCloseUserMenu}
              >
                {settings.map((setting) => (
                  <MenuItem
                    key={setting}
                    onClick={() => handleCloseNavMenu(setting)}
                  >
                    <Typography textAlign="center">{setting}</Typography>
                  </MenuItem>
                ))}
              </Menu>
            </Box>
          </Toolbar>
        </Container>

      </AppBar>

    </div>
  );
};
export default Navbar;
