import PostText from './PostText';
import * as React from 'react';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setUpdate } from '../features/feedSlice';
import api from '../api/axios';

const PostCreate = () => {
  const dispatch = useDispatch();
  const [text, setText] = useState("");
  const userOb = useSelector((state) => state.auth.userOb);

  const handlePost = (text) => {
    api
      .post("/post", {
        user: userOb._id,
        description: text,
      })
      .then((res) => {
        dispatch(setUpdate());
      })
      .catch((err) => {
        console.log("Error: ", err);
      });

  }

  return (
    <Stack spacing={1} direction="row" sx={{ display: 'flex', width: '100%' }}>
       avatar={
                    <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe" onClick={() => { findProfile(_id) }}>
                        <img src={avatar}></img>
                    </Avatar>
                }
      <PostText setText={setText} placeholder={"Tell us your story"} />
      <Button variant="contained" size="small" sx={{ borderRadius: '20px' }} onClick={() => { handlePost(text); }}>Post</Button>
    </Stack>
  );
}

export default PostCreate;
