import { useEffect, useState } from "react";
import Post from "./Post";
import PostCreate from "./PostCreate";
import Stack from '@mui/material/Stack';
import api from "../api/axios";
import { Grid } from '@mui/material';
import Friends from "./Friends";



const Feed = ({ handlePost, handleComment, userID, userOb, setClickedProfile }) => {
    const [posts, setPosts] = useState([]);
    const [update, setUpdate] = useState(false);

    useEffect(() => {
        api
        .get("/feed?user=" + userID)
        .then((res) => {
            setPosts(res.data);
        })
        .catch((err) => {
            console.log("Error: ", err);
        });
    }
        , [update]);
    const { friendPosts, myPosts } = (posts === undefined || null) ? { friendPosts: [], myPosts: [] } : posts;
    const totalPosts = friendPosts?.concat(myPosts);

    const findProfile = (user) => {
      api.get("/getprofile", {
          params: {
              user: user,
          },
      }).then((res) => {
          setClickedProfile(res.data[0])
          
          user===userId ? navigate("/myprofile") : navigate("/profile")
      })
  }

    return (
      <div>
        <Friends friendslist={userOb.friends}/>
        <Grid container direction="column" spacing={2} style={{ maxWidth: '1500px', margin: '0 auto' }}>

          <Grid item>
            <PostCreate handlePost={handlePost} setUpdate={setUpdate} update={update} />
          </Grid>
          {totalPosts?.map(post => (
            <Grid item key={post._id}>
              <Post key={post._id} findProfile={findProfile} handleComment={handleComment} setUpdate={setUpdate} setClickedProfile={setClickedProfile} update={update} post={post} userId={userID} />
            </Grid>
          ))}
        </Grid>
        </div>
      );
    };
    

export default Feed;
