import React from 'react';
import {
  MDBRow,
  MDBCol,
  MDBInput,
  MDBCheckbox,
  MDBBtn,
  MDBIcon,
  MDBTabs,
  MDBTabsItem,
  MDBTabsLink,
  MDBTabsContent,
  MDBTabsPane
} from 'mdb-react-ui-kit';
import BirthdayPicker from './BirthdayPicker';
import { useState } from "react";
import Alert from "@mui/material/Alert";



export default function RegisterCheck({handleSubmit}) {
    const [email, setEmail] = useState("");
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [rePassword, setRePassword] = useState("");
    const [name, setName] = useState("");
    const [date, setDate] = useState();
    const [address, setAddress] = useState("");
    const [alertMsg, setAlertMsg] = useState("");
    const [loginRegisterActive, setLoginRegisterActive] = useState('register');
    const [samePass, setSamePass] = useState();

    const displayAlert = () => {
      if (alertMsg === "") return "";
      if (!alertMsg.includes("succesfully")) {
        return <Alert severity="error">{alertMsg}</Alert>;
      } else {
        return <Alert severity="success">{alertMsg}</Alert>;
      }
    };

    const PasswordCheck = () => {
        if (password === rePassword) {
            setSamePass(true);
        }
        else {
            setSamePass(false);
        }
        }
  return (
    <div className='ml7 mr7 mt3 shadow-4'>
        <div className='pa4'>
      <MDBTabs pills justify className='mb-3'>
        <MDBTabsItem>
          <MDBTabsLink
            onClick={() => setLoginRegisterActive('login')}
            active={loginRegisterActive === 'login'}
          >
            Login
          </MDBTabsLink>
        </MDBTabsItem>
        <MDBTabsItem>
          <MDBTabsLink
            onClick={() => setLoginRegisterActive('register')}
            active={loginRegisterActive === 'register'}
          >
            Register
          </MDBTabsLink>
        </MDBTabsItem>
      </MDBTabs>

      <MDBTabsContent>
        <MDBTabsPane show={loginRegisterActive === 'login'}>
          <form>
            <div className='text-center mb-3'>
              <p>Sign up with:</p>

              <MDBBtn floating color="secondary" className='mx-1'>
                <MDBIcon fab icon='facebook-f' />
              </MDBBtn>

              <MDBBtn floating color="secondary" className='mx-1'>
                <MDBIcon fab icon='google' />
              </MDBBtn>

              <MDBBtn floating color="secondary" className='mx-1'>
                <MDBIcon fab icon='twitter' />
              </MDBBtn>

              <MDBBtn floating color="secondary" className='mx-1'>
                <MDBIcon fab icon='github' />
              </MDBBtn>
            </div>

            <p className='text-center'>or:</p>

            <MDBInput className='mb-4' type='email' id='form7Example1' label='Email address' />
            <MDBInput className='mb-4' type='password' id='form7Example2' label='Password' />

            <MDBRow className='mb-4'>
              <MDBCol className='d-flex justify-content-center'>
                <MDBCheckbox id='form7Example3' label='Remember me' defaultChecked />
              </MDBCol>
              <MDBCol>
                <a href='#!'>Forgot password?</a>
              </MDBCol>
            </MDBRow>

            <MDBBtn type='submit' className='mb-4' block >
              Sign in
            </MDBBtn>

            <div className='text-center'>
              <p>
                Not a member? <a href='#!'>Register</a>
              </p>
            </div>
          </form>
        </MDBTabsPane>
        <MDBTabsPane show={loginRegisterActive === 'register'}>
          <form>
            <div className='text-center mb-3'>
              <p>Sign up with:</p>

              <MDBBtn floating color="secondary" className='mx-1'>
                <MDBIcon fab icon='facebook-f' />
              </MDBBtn>

              <MDBBtn floating color="secondary" className='mx-1'>
                <MDBIcon fab icon='google' />
              </MDBBtn>

              <MDBBtn floating color="secondary" className='mx-1'>
                <MDBIcon fab icon='twitter' />
              </MDBBtn>

              <MDBBtn floating color="secondary" className='mx-1'>
                <MDBIcon fab icon='github' />
              </MDBBtn>
            </div>

            <p className='text-center'>or:</p>

            <MDBInput className='mb-4' id='form8Example1' label='Full Name' onChange={(e) => {setName(e.target.value)}} />
            <MDBInput className='mb-4' id='form8Example2' label='Username' onChange={(e) => {setUsername(e.target.value)}} />
            <MDBInput className='mb-4' type='email' id='form8Example3' label='Email address' onChange={(e) => {setEmail(e.target.value)}} />
            <MDBInput className='mb-4' type='password' id='form8Example4' label='Password' onChange={(e) => {setPassword(e.target.value)}} />
            <MDBInput className='mb-4' type='password' id='form8Example5' label='Repeat password' onChange={(e) => {setRePassword(e.target.value)}}/>
            <MDBInput className='mb-4' id='form8Example1' label='Address'  onChange={(e) => {setAddress(e.target.value)}}/>
            <BirthdayPicker date={date} setDate={setDate}/>

            <MDBCheckbox
              wrapperClass='d-flex justify-content-center mb-4'
              id='form8Example6'
              label='I have read and agree to the terms'
              defaultChecked
            />

            <MDBBtn type='submit' className='mb-4' block>
              Sign in
            </MDBBtn>
          </form>
        </MDBTabsPane>
      </MDBTabsContent>
      </div>
    </div>
  );
}
