import PostText from './PostText';
import * as React from 'react';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import { useState } from 'react';


const PostCreate = ({handlePost, setUpdate, update}) => {
    const [text, setText] = useState("");

    return (
     <Stack spacing={1} direction="row" sx={{display: 'flex', width: '100%'}}>
    <PostText setText={setText} placeholder={"Tell us your story"}/>
    <Button variant="contained" size="small" sx={{ borderRadius:'10px'}} onClick={() => {handlePost(text); setUpdate(!update)}}>Post</Button>
    </Stack>
  );
}

export default PostCreate;
