// import "./App.css";
import Navbar from "./components/Navbar";
import Feed from "./components/Feed";
import Register from "./components/Register";
import Login from "./components/Login";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [user, setUser] = useState("omer223@gmail.com");
  const [userID, setUserID] = useState("6508b4bf3e4f921773fc79bf");
  const [posts, setPosts] = useState([]);

  const api = axios.create({
    baseURL: "http://localhost:3001",
    Headers: {
      "Access-Control-Allow-Origin": "*",
    },
  });



  const handleSubmit = (email, password, name, setAlertMsg) => {
    if (name === "") {
      setAlertMsg("Please fill in your name");
      return;
    } else if (email === "") {
      setAlertMsg("Please fill in your email address");
      return;
    } else if (password === "") {
      setAlertMsg("Please fill in your password");
      return;
    } else {
      if (name === null) {
        handleLogin(email, password, setAlertMsg);
      } else {
        handleRegister(email, password, name, setAlertMsg);
      }
    }
  };

  const handlePost = (text) => {
    api
      .post("/post", {
        user: user,
        description: text,
      })
      .then((res) => {
        // getFeed();
      })
      .catch((err) => {
        console.log("Error: ", err);
      });
  }

  const handleLogin = (email, password, setAlertMsg) => {
    api
      .post("/login", {
        email: email,
        password: password,
      })
      .then((res) => {
        setAlertMsg("You have logged in successfully!");
        setIsLoggedIn(true);
        setUser(email);
        setUserID(res.data.id);
      })
      .catch((err) => {
        setAlertMsg("Wrong email or password, please try again!");
      });
  };
  const handleRegister = (email, password, name, setAlertMsg) => {
    api
      .post("/register", {
        email: email,
        password: password,
        name: name,
      })
      .then((res) => {
        setAlertMsg("You have signed up to our service succesfully!");
      })
      .catch((err) => {
        setAlertMsg("This email address is already in use!");
      });
  };


  return (
    <Router>
      <Navbar />
      <Routes>
        <Route
          path="/"
          element={
            isLoggedIn ?
              <Feed getFeed={getFeed} userID={userID} handlePost={handlePost} />
              : <Login handleSubmit={handleSubmit} />
          }
        />
        <Route path="/login" element={<Login handleSubmit={handleSubmit} />} />
        <Route
          path="/register"
          element={<Register handleSubmit={handleSubmit} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
