import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Checkbox from '@mui/material/Checkbox';
import Avatar from '@mui/material/Avatar';
import { useEffect, useState } from "react";
import api from "../api/axios";
import ListSubheader from '@mui/material/ListSubheader';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';


export default function Friends({ friendslist , findProfile }) {
    const [friends,setFriends] = useState([])
    useEffect(() => {
        api
            .get("/getprofile", {
                params: {
                    user: friendslist,
                },
            }).then((res) => {
                setFriends(res.data)
            }).catch((err) => {
                console.log("Error: ", err);
            });
    }
        , []);



        return (
            <Card  className='ba shadow-1' dense title="Friends" sx={{ width: '400%', maxWidth: 360, bgcolor: 'background.paper', borderRadius:'20px', overflow: 'hidden'}}>
             <CardContent>
            <List 
            subheader={<ListSubheader>Friends</ListSubheader>}>
                {friends.map((value) => {
                    const labelId = `checkbox-list-secondary-label-${value.name}`;
                    return (
                        <ListItem
                            key={value.name}
                            disablePadding
                            onClick={() => { findProfile(value._id) }}
                        >
                            <ListItemButton>
                                <ListItemAvatar>
                                    <Avatar
                                        alt={`Avatar n°${value.name}`}
                                        src={`${value.avatar}`}
                                    />
                                </ListItemAvatar>
                                <ListItemText id={labelId} primary={`${value.name}`} />
                            </ListItemButton>
                        </ListItem>
                    );
                })}
            </List>
            </CardContent>
            </Card>
        );
    }
