import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import { useEffect, useState } from "react";
import api from "../api/axios";
import ListSubheader from '@mui/material/ListSubheader';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import { IconButton } from '@mui/material';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import PersonRemoveIcon from '@mui/icons-material/PersonRemove';
import ChatIcon from '@mui/icons-material/Chat';
import Divider from '@mui/material/Divider';
import { useSelector, useDispatch } from 'react-redux';
import { setUpdate } from '../features/feedSlice';
import { setUserOb } from '../features/authSlice';

export default function Friends() {
    const dispatch = useDispatch();
    const [friends, setFriends] = useState([])
    const [anchorEl, setAnchorEl] = React.useState(null);
    const userOb = useSelector((state) => state.auth.userOb);
    const userID = userOb._id;
    const update = useSelector((state) => state.feed.update);


    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };


    useEffect(() => {
        api
            .get("/getfriends", {
                params: {
                    user: userID,
                },
            }).then((res) => {
                setFriends(res.data);
             
                
            }).catch((err) => {
                console.log("Error: ", err);
            });
    }
        , [update]);



    const removeFriend = (friendId) => {
        api
            .post("/removefriend", {
                userId: userID,
                friendId: friendId,
            })
            .then((res) => {
                dispatch(setUpdate());
            })
            .catch((err) => {
                console.log("Error: ", err);
            });
    }
    return (
        <List className='shadow-3' dense sx={{ width: '400%', maxWidth: 360, bgcolor: 'background.paper', borderRadius: '20px', overflow: 'hidden' }}
            subheader={<ListSubheader>Friends</ListSubheader>}>
            {friends.map((value) => {
                const labelId = `checkbox-list-secondary-label-${value.name}`;
                return (
                    <ListItem
                        key={value.name}
                        disablePadding

                    >
                        <ListItemButton>
                            <ListItemAvatar>
                                <Avatar
                                    alt={`Avatar n°${value.name}`}
                                    src={`${value.avatar}`}
                                />
                            </ListItemAvatar>
                            <ListItemText id={labelId} primary={`${value.name}`} />

                        </ListItemButton>

                        <div>
                            <IconButton aria-label="settings" onClick={handleClick}  >
                                <MoreVertIcon />
                            </IconButton>
                            <Menu
                                id="simple-menu"
                                anchorEl={anchorEl}
                                open={Boolean(anchorEl)}
                                onClose={handleClose}
                                style={{ borderRadius: '20px' }}
                                sx={{
                                    '.MuiPaper-root': {
                                        borderRadius: '10px',
                                    },
                                }}
                            >
                                <MenuItem onClick={() => {

                                    handleClose();
                                }}><ChatIcon style={{ color: 'blue', marginRight: 10 }} />Send Message</MenuItem>
                                <Divider sx={{ my: 0.5 }} />
                                <MenuItem onClick={() => {
                                    handleClose();
                                    removeFriend(value._id)
                                }}
                                ><PersonRemoveIcon style={{ color: 'red', marginRight: 10 }} />Remove Friend</MenuItem>
                            </Menu>
                        </div>


                    </ListItem>
                );
            })}
        </List>
    );
}
