import * as React from 'react';
import {useState, useEffect} from 'react'
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import { useSelector } from 'react-redux'

export default function ChatMessage({ message }) {
    const {user,setUser} = useState(null)
    const currentChat = useSelector((state) => state.chat.currentChat);
    useEffect(() => {
        if (currentChat.users[0]._id === message.userId)
            setUser(currentChat.users[0])
        else
            setUser(currentChat.users[1])
    }, [])
    return (
        <ListItem alignItems="flex-start" >
            <ListItemAvatar>
                <Avatar alt={message._userId === currentChat.users[0] ? currentChat.Users[0].name : currentChat.Users[1].name} src={message._userId === currentChat.users[0] ? currentChat.Users[0].name : currentChat.Users[1].name} />
            </ListItemAvatar>
            <ListItemText
                primary={message._userId === currentChat.users[0] ? currentChat.Users[0].name : currentChat.Users[1].name}
                secondary={
                    <React.Fragment>
                        <Typography
                            sx={{ display: 'inline' }}
                            component="span"
                            variant="body2"
                            color="text.primary"
                        >
                            {message.content}
                        </Typography>
                       
                    </React.Fragment>
                }
            />
        </ListItem>
    );
}
