import * as React from 'react';
import Box from '@mui/material/Box';
import {TextField, makeStyles } from '@mui/material';

const useStyles = makeStyles((theme) => ({
  roundedInput: {
      '& .MuiOutlinedInput-root': {
          borderRadius: '15px',
      },
  },
}));

export default function PostText({setText, placeholder}) {
  const classes = useStyles();

  return (
    <Box
      component="form"
      sx={{
   
        display: 'flex',
        flex: 1, // Makes the Box (and hence the TextField inside it) expand to fill the available space
        '& .MuiTextField-root': { 
          m: 0, 
          width: '100%', // Makes the TextField expand to fill the Box
        },
      }}
      noValidate
      autoComplete="off"
    >
    
        <TextField
          
          id="outlined-multiline-flexible"
          label={placeholder}
          onChange={(event)=>setText(event.target.value)}
          multiline
          size="small"
          maxRows={3}
     
          className={classes.roundedInput}
          />
    </Box>
  );
}
