import PostText from './PostText';
import * as React from 'react';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setUpdate } from '../features/feedSlice';
import Avatar from '@mui/material/Avatar';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import api from '../api/axios';
import PhotoIcon from '@mui/icons-material/Photo';
import { sendMessage } from '../api/socketManager'
import { addMessage, removeMessage } from '../features/chatSlice';
import { v4 as uuidv4 } from 'uuid';


const ChatSendMessage = () => {
    const dispatch = useDispatch();
    const [text, setText] = useState("");
    const userOb = useSelector((state) => state.auth.userOb);

    const handlePost = (text) => {
        if (text === "") return;
        dispatch(addMessage({ userId: userOb, content: text, created_at: new Date() }))
        const uniqueid = uuidv4();
        sendMessage('sendMessage', {  uniqueid, userId: userOb._id, friendId: userOb._id, content: text }, (response) => {
            if (!response.success) {
                dispatch(removeMessage({ _id: uniqueid, userId: userOb, content: text, created_at: new Date() }))
            } else {
                console.log("Message sent")
            }
        }
        )

    }

    return (
        <Card sx={{ borderRadius: '15px' }}>
            <CardContent>
                <Stack spacing={1} direction="row" sx={{ display: 'flex', width: '100%' }}>
                    <Avatar aria-label="recipe">
                        <img src={userOb.avatar}></img>
                    </Avatar>

                    <PostText setText={setText} placeholder={"Write you message here"} />
                    <Button variant="contained" size="small" sx={{ borderRadius: '20px' }} onClick={() => { handlePost(text); }}>Send</Button>

                </Stack>
            </CardContent>
        </Card>
    );
}

export default ChatSendMessage;
