import { useEffect, useState } from "react";
import Post from "./Post";
import PostCreate from "./PostCreate";
import Stack from '@mui/material/Stack';
import api from "../api/axios";
import { Grid } from '@mui/material';
import Friends from "./Friends";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import { setUpdate, setPosts, setClickedProfile } from '../features/feedSlice';



const Feed = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const update = useSelector((state) => state.feed.update);
    const posts = useSelector((state) => state.feed.posts);
    useEffect(() => {
        api
            .get("/feed?user=" + userID)
            .then((res) => {
                dispatch(setPosts(res.data));
            })
            .catch((err) => {
                console.log("Error: ", err);
            });
    }
        , [update]);


    const userOb = useSelector((state) => state.auth.userOb);
    const userID = userOb._id;
    const { friendPosts, myPosts } = (!posts) ? { friendPosts: [], myPosts: [] } : posts;
    const totalPosts = friendPosts?.concat(myPosts);
    const totalPostsSorted = totalPosts?.sort((a, b) => {
        return new Date(b.date) - new Date(a.date);
    });
    const findProfile = (user) => {
        console.log(user)
        api.get("/getprofile", {
            params: {
                user: user,
            },
        }).then((res) => {
            dispatch(setClickedProfile(res.data))
            user === userID ? navigate("/myprofile") : navigate("/profile")
        })
    }

    return (
        <div style={{ position: 'relative', marginTop: '20px', width: '100%', display: 'flex', justifyContent: 'center' }}>

            <div style={{ position: 'absolute', left: 10 }}>
                <Friends  findProfile={findProfile} />
            </div>

            <div style={{ width: '60%' }}>

                <PostCreate setUpdate={setUpdate} update={update} style={{ width: '100%', maxWidth: '750px', margin: '0 auto' }} />

                <Grid container direction="column" spacing={2} sx={{ marginTop: '10px' }}>
                    {totalPostsSorted?.map(post => (
                        <Grid item key={post._id} xs={12}>
                            <Post key={post._id} findProfile={findProfile} post={post}  style={{ width: '100%', maxWidth: '750px', margin: '0 auto' }} />
                        </Grid>
                    ))}
                </Grid>

            </div>

        </div>

    );
};


export default Feed;
