import { useEffect, useState } from "react";
import Post from "./Post";
import PostCreate from "./PostCreate";
import Stack from '@mui/material/Stack';
import api from "../api/axios";
import { Grid } from '@mui/material';
import Friends from "./Friends";
import { useNavigate } from "react-router-dom";


const Feed = ({ handlePost, handleComment, userID, userOb, setClickedProfile }) => {
    const [posts, setPosts] = useState([]);
    const [update, setUpdate] = useState(false);
    const navigate = useNavigate();
    
    useEffect(() => {
        api
        .get("/feed?user=" + userID)
        .then((res) => {
            setPosts(res.data);
        })
        .catch((err) => {
            console.log("Error: ", err);
        });
    }
        , [update]);
    const { friendPosts, myPosts } = (posts === undefined || null) ? { friendPosts: [], myPosts: [] } : posts;
    const totalPosts = friendPosts?.concat(myPosts);

    const findProfile = (user) => {
      api.get("/getprofile", {
          params: {
              user: user,
          },
      }).then((res) => {
          setClickedProfile(res.data[0])
          user===userID ? navigate("/myprofile") : navigate("/profile")
      })
  }

    return (
<div style={{ display: 'flex', marginTop: '20px', width: '100%' }}>
    
    {/* Friends Component on the Far Left */}
    <div style={{ marginRight: '20px' }}>
        <Friends friendslist={userOb.friends} findProfile={findProfile} />
    </div>

    {/* Centered Container for Posts */}
    <div style={{ width: '800px' }}>
        
        {/* PostCreate component */}
        <div style={{ display: 'flex', justifyContent: 'center' }}>
            <PostCreate handlePost={handlePost} setUpdate={setUpdate} update={update} />
        </div>

        {/* Posts */}
        <Grid container direction="column" spacing={2}>
            {totalPosts?.map(post => (
                <Grid item key={post._id} xs={12} style={{ display: 'flex', justifyContent: 'center' }}>
                    <Post key={post._id} findProfile={findProfile} handleComment={handleComment} setUpdate={setUpdate} setClickedProfile={setClickedProfile} update={update} post={post} userId={userID} />
                </Grid>
            ))}
        </Grid>

    </div>
    
    {/* Remaining space on the right */}
    <div style={{ flex: 1 }}></div>

</div>




      );
    };
    

export default Feed;
