import { useEffect } from 'react'
import * as React from 'react';
import { useSelector } from 'react-redux'
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import { registerMessageHandler, unregisterMessageHandler } from '../api/socketManager';
import ListSubheader from '@mui/material/ListSubheader';

export default function FriendsChats() {

    const user = useSelector((state) => state.auth.userOb);
    const friends = user.friends;
    return (
        <List className="shadow-5" sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }} subheader={<ListSubheader>Friends</ListSubheader>}>
            {!friends ? null : friends.map((friend) => {
                <ListItem key={friend._id} alignItems="flex-start">
                    <ListItemAvatar>
                        <Avatar alt={friend.name} src={friend.avatar} />
                    </ListItemAvatar>
                    <ListItemText
                        primary={friend.name}
                        secondary={
                            <React.Fragment>
                                <Typography
                                    sx={{ display: 'inline' }}
                                    component="span"
                                    variant="body2"
                                    color="text.primary"
                                >
                                    Ali Connors
                                </Typography>
                                {" — I'll be in your neighborhood doing errands this…"}
                                </React.Fragment>
                        }
                    />
                </ListItem>

            }
            )}

            <Divider variant="inset" component="li" />
        </List>
    );
}
