import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import { useEffect, useState } from "react";
import api from "../api/axios";
import ListSubheader from '@mui/material/ListSubheader';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import { IconButton } from '@mui/material';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import PersonRemoveIcon from '@mui/icons-material/PersonRemove';
import ChatIcon from '@mui/icons-material/Chat';
import Divider from '@mui/material/Divider';
import { useSelector, useDispatch } from 'react-redux';
import { setUpdate } from '../features/feedSlice';
import { setAlertMsg } from '../features/authSlice';
import FiberManualRecordIcon from '@mui/icons-material/FiberManualRecord';
import { registerMessageHandler, unregisterMessageHandler, sendMessage } from '../api/socketManager';


export default function Friends({ findProfile, removeFriend }) {
    const userOb = useSelector((state) => state.auth.userOb);
    const userID = userOb._id;
    const dispatch = useDispatch();
    const [friends, setFriends] = useState(userOb.friends)
    const [anchorEls, setAnchorEls] = useState({});
    const [refresh, setRefresh] = useState(false)
    const update = useSelector((state) => state.feed.update);

    useEffect(() => {
        api
            .get("/getfriends", {
                params: {
                    user: userID,
                },
            }).then((res) => {
                const friends = res.data;
                const friendsIds = friends.map(friend => friend._id);
                sendMessage('checkOnline', friendsIds, (response) => {

                    const updatedFriends = friends.map(friend => {
                        if (response.data.includes(friend._id)) {
                            return {
                                ...friend,
                                online: true
                            };
                        }
                        return friend;
                    });
                    setFriends(updatedFriends);
                });

                


            }).catch((err) => {
                console.log("Error: ", err);
            });

        const handleConnect = (data) => {
            const userId = data;
            const updatedFriends = friends.map(friend => {
                if (friend._id === userId) {
                    return {
                        ...friend,
                        online: true
                    };
                }
                return friend;
            });
            setFriends(updatedFriends);
            setRefresh(!refresh);
        }

        const handleDisconnect = (data) => {
            const userId = data;
            const updatedFriends = friends.map(friend => {
                if (friend._id === userId) {
                    return {
                        ...friend,
                        online: false
                    };
                }
                return friend;
            });
            setFriends(updatedFriends);
            setRefresh(!refresh);
        }

        registerMessageHandler('online', handleConnect);
        registerMessageHandler('offline', handleDisconnect);

        return () => {
            unregisterMessageHandler('online');
            unregisterMessageHandler('offline');
        }

    }
        , [update]);


    const handleClick = (event, id) => {
        setAnchorEls(prev => ({ ...prev, [id]: event.currentTarget }));
    };

    const handleClose = (id) => {
        setAnchorEls(prev => ({ ...prev, [id]: null }));
    };




    return (
        <List className='shadow-3' dense sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper', borderRadius: '20px', overflow: 'hidden' }}
            subheader={<ListSubheader>Friends</ListSubheader>}>
            {friends.map((value) => {
                return (
                    <ListItem
                        key={value._id}
                        disablePadding

                    >
                        {value.online ? (
                            <FiberManualRecordIcon sx={{ marginLeft: 1, color: 'green' }} fontSize='small' />
                        ) :
                            <FiberManualRecordIcon sx={{ marginLeft: 1 }} fontSize='small' />
                        }

                        <ListItemButton onClick={() => findProfile(value._id)}>
                            <ListItemAvatar>
                                <Avatar
                                    alt={`Avatar n°${value.name}`}
                                    src={`${value.avatar}`}
                                />
                            </ListItemAvatar>
                            <ListItemText id={value.id} primary={`${value.name}`} />

                        </ListItemButton>

                        <div>
                            <IconButton aria-label="settings" onClick={(event) => handleClick(event, value._id)} >
                                <MoreVertIcon />
                            </IconButton>
                            <Menu
                                id="simple-menu"
                                anchorEl={anchorEls[value._id]}
                                open={Boolean(anchorEls[value._id])}
                                onClose={() => handleClose(value._id)}
                                style={{ borderRadius: '20px' }}
                                sx={{
                                    '.MuiPaper-root': {
                                        borderRadius: '10px',
                                    },
                                }}
                            >
                                <MenuItem onClick={() => {

                                    handleClose();
                                }}><ChatIcon style={{ color: 'blue', marginRight: 10 }} />Send Message</MenuItem>
                                <Divider sx={{ my: 0.5 }} />
                                <MenuItem onClick={(e) => {
                                    removeFriend(value._id);
                                    handleClose(value._id);


                                }}
                                ><PersonRemoveIcon style={{ color: 'red', marginRight: 10 }} />Remove Friend</MenuItem>
                            </Menu>
                        </div>


                    </ListItem>
                );
            })}
        </List>
    );
}
