import * as React from 'react';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Avatar from '@mui/material/Avatar';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
import { styled } from '@mui/material/styles';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import api from "../api/axios";
import MoreVertIcon from '@mui/icons-material/MoreVert';
import AddCommentIcon from '@mui/icons-material/AddComment';
import CommentCreate from './CommentCreate';
import Comment from './Comment';
import Badge from '@mui/material/Badge';
import MailIcon from '@mui/icons-material/Mail';


import Divider from '@mui/material/Divider';

const Post = ({ post, userId, setUpdate, update }) => {
    const [expanded, setExpanded] = React.useState(false);
    const { description, date, likes, comments } = post;
    const { name, email, _id } = post.userId;

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    const ExpandMore = styled((props) => {
        const { expand, ...other } = props;
        return <IconButton {...other} />;
    })(({ theme, expand }) => ({
        transform: !expand ? 'rotate(0deg)' : 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    }));

    let postDate;
    let todayDate;
    let dates
    let times;

    const compareDates = (postDateA) => {
        const postFullDate = new Date(postDateA);
        const todayFullDate = new Date()
        postDate = new Date(postFullDate.getFullYear(), postFullDate.getMonth(), postFullDate.getDate());
        todayDate = new Date(todayFullDate.getFullYear(), todayFullDate.getMonth(), todayFullDate.getDate());
        if (postDate.getTime() === todayDate.getTime()) {
            dates = date.toString().substring(0, 10);
            times = date.toString().substring(11, 16);
            return true;
        }
        return false;
    }

    const like = () => {
        api.post("/like", { user: userId, post: post._id })
            .then((res) => {
                setUpdate(!update);
            })
            .catch((err) => {
                console.log("Error: ", err);
            });
    }
    const unlike = () => {
        api.post("/unlike", { user: userId, post: post._id })
            .then((res) => {
                setUpdate(!update);

            })
            .catch((err) => {
                console.log("Error: ", err);
            });
    }

    const handleComment = (text) => {
        api
            .post("/comment", {
                postId: post._id,
                userId: userId,
                content: text,
                create_at: new Date(),
            })
            .catch((err) => {
                console.log("Error: ", err);
            });
    }

    return (
        <Card sx={{ maxWidth: "180ch" }}>
            <CardHeader
                avatar={
                    <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
                        R
                    </Avatar>
                }

                action={
                    userId === _id ? (
                        <IconButton aria-label="settings"  >
                            <MoreVertIcon />
                        </IconButton>
                    ) : null
                }
                title={name}
                subheader={compareDates(date) ? times : dates + " " + times}
            />
            <CardContent>
                <Typography variant="body2" color="text.secondary">
                    {description}

                </Typography>
            </CardContent>
            <CardActions disableSpacing>
                <IconButton aria-label="like">
                    {likes.includes(userId) ? <FavoriteIcon  style={{ color: 'red' }} onClick={() => unlike()} /> : <FavoriteBorderIcon onClick={() => like()} />}
                </IconButton>

                <ExpandMore
                    expand={expanded}
                    onClick={handleExpandClick}
                    aria-expanded={expanded}
                    aria-label="show more"
                >
                 

                    <Badge badgeContent={comments.length} color="primary">
                    <AddCommentIcon  />
                    </Badge>
                </ExpandMore>

            </CardActions>
            <Divider variant="middle" />
            <Collapse in={expanded} timeout="auto" unmountOnExit>
                <CardContent  >
                    <CommentCreate handleComment={handleComment} setUpdate={setUpdate} update={update} />
                    <div style={{ maxHeight: "700px", overflowY: "scroll" }}>

                        {comments.length ? (comments.map((comment, i) => {
                            return (
                                <Comment key={i} comment={comment} compareDates={compareDates} userID={userId} />
                            )
                        })
                        ) : "No comments yet"}
                    </div>

                </CardContent>
            </Collapse>
        </Card>
    );
}

export default Post;
