import * as React from 'react';
import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';

import MoreVertIcon from '@mui/icons-material/MoreVert';



const Post = ({ post, userId }) => {

    const { description, date, likes, comments } = post;
    const { name, email, _id } = post.userId;
    let dates = date.toString().substring(0, 10);
    let times = date.toString().substring(11, 16);

    const like = () => {
        api.post("/like", { user: userId, post: _id })
            .then((res) => {
                console.log(res.data);
            })
            .catch((err) => {
                console.log("Error: ", err);
            });
    }
    const unlike = () => {
        api.post("/unlike", { user: userId, post: _id })
            .then((res) => {
                console.log(res.data);
            })
            .catch((err) => {
                console.log("Error: ", err);
            });
    }
    return (
        <Card sx={{ maxWidth: "180ch" }}>
            <CardHeader
                avatar={
                    <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
                        R
                    </Avatar>
                }

                action={
                    userId === _id ? (
                        <IconButton aria-label="settings"  >
                            <MoreVertIcon />
                        </IconButton>
                    ) : null
                }
                title={name}
                subheader={dates + " " + times}
            />
            <CardContent>
                <Typography variant="body2" color="text.secondary">
                    {description}

                </Typography>
            </CardContent>
            <CardActions disableSpacing>
                <IconButton aria-label="like">
                    {likes.includes(userId) ? <FavoriteIcon onClick={() => unlike()} /> : <FavoriteBorderIcon onClick={() => unlike()} />}
                </IconButton>

            </CardActions>
        </Card>
    );
}

export default Post;
