import { useEffect, useState } from "react";
import Post from "./Post";
import PostCreate from "./PostCreate";
import Stack from '@mui/material/Stack';
import api from "../api/axios";
import { Grid } from '@mui/material';



const Feed = ({ handlePost, handleComment, userID, setClickedProfile }) => {
    const [posts, setPosts] = useState([]);
    const [update, setUpdate] = useState(false);

    useEffect(() => {
        api
        .get("/feed?user=" + userID)
        .then((res) => {
            setPosts(res.data);
            console.log(res.data)
        })
        .catch((err) => {
            console.log("Error: ", err);
        });
    }
        , [update]);
    const { friendPosts, myPosts } = (posts === undefined || null) ? { friendPosts: [], myPosts: [] } : posts;
    const totalPosts = friendPosts?.concat(myPosts);

    return (
      
        <Grid container direction="column" spacing={2} style={{ maxWidth: '1500px', margin: '0 auto' }}>

          <Grid item>
            <PostCreate handlePost={handlePost} setUpdate={setUpdate} update={update} />
          </Grid>
          {totalPosts?.map(post => (
            <Grid item key={post._id}>
              <Post key={post._id} handleComment={handleComment} setUpdate={setUpdate} setClickedProfile={setClickedProfile} update={update} post={post} userId={userID} />
            </Grid>
          ))}
        </Grid>
      );
    };
    

export default Feed;
