import { useEffect, useState } from "react";
import Post from "./Post";
import PostCreate from "./PostCreate";
import Stack from '@mui/material/Stack';
import api from "../api/axios";

const Feed = ({ handlePost, userID }) => {
    const [posts, setPosts] = useState([]);
    const [update, setUpdate] = useState(0);


   

    useEffect(() => {
        api
        .get("/feed?user=" + userID)
        .then((res) => {
            setPosts(res.data);
            console.log(res.data)
        })
        .catch((err) => {
            console.log("Error: ", err);
        });
    }
        , []);
    const { friendPosts, myPosts } = (posts === undefined || null) ? { friendPosts: [], myPosts: [] } : posts;
    const totalPosts = friendPosts?.concat(myPosts);

    return (
        <div className=" mt3 center">
            <Stack spacing={3} direction="column">
                <PostCreate handlePost={handlePost} setUpdate={setUpdate} />
                {totalPosts?.map(post => <Post key={post._id} post={post} userId={userID} />)}
            </Stack>
        </div>
    );
};

export default Feed;
