import { useEffect, useState } from "react";
import Post from "./Post";
import PostCreate from "./PostCreate";
import Stack from "@mui/material/Stack";
import api from "../api/axios";
import { Grid } from "@mui/material";
import Friends from "./Friends";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { setAlertMsg } from "../features/authSlice";
import { setUpdate, setPosts, setClickedProfile } from "../features/feedSlice";
import Divider from "@mui/material/Divider";

const Feed = ({ removeFriend }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const update = useSelector((state) => state.feed.update);
  const posts = useSelector((state) => state.feed.posts);
  useEffect(() => {
    api
      .get("/feed?user=" + userID)
      .then((res) => {
        dispatch(setPosts(res.data));
      })
      .catch((err) => {
        console.log("Error: ", err);
      });
  }, [update]);

  dispatch(setAlertMsg(""));
  const userOb = useSelector((state) => state.auth.userOb);
  const userID = userOb._id;
  const { friendPosts, myPosts } = !posts
    ? { friendPosts: [], myPosts: [] }
    : posts;
  const totalPosts = friendPosts?.concat(myPosts);
  const totalPostsSorted = totalPosts?.sort((a, b) => {
    return new Date(b.date) - new Date(a.date);
  });
  const findProfile = (user) => {
    console.log(user);
    api
      .get("/getprofile", {
        params: {
          user: user,
        },
      })
      .then((res) => {
        dispatch(setClickedProfile(res.data));
        user === userID ? navigate("/myprofile") : navigate("/profile");
      });
  };

  return (
    <div style={{ marginTop: "20px", width: "100%" }}>
      <Grid container spacing={3}>
        {/* Friends Component */}
        <Grid item xs={12} md={2}>
          <Friends findProfile={findProfile} removeFriend={removeFriend} />
        </Grid>
  
        {/* PostCreate and Posts */}
        <Grid item xs={12} md={6}>
          <PostCreate setUpdate={setUpdate} update={update} />
  
          <Stack spacing={2} sx={{ marginTop: "10px" }}>
            {totalPostsSorted?.map((post) => (
              <Post key={post._id} findProfile={findProfile} post={post} />
            ))}
          </Stack>
        </Grid>
  
        {/* Empty Space on the Right */}
        <Grid item xs={12} md={2}>
          {/* This is left intentionally empty for the desired layout. */}
        </Grid>
      </Grid>
    </div>
  );
  
};

export default Feed;
