import Navbar from "./components/Navbar";
import Feed from "./components/Feed";
import RegisterCheck from "./components/RegisterCheck";
import Profile from "./components/Profile";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useEffect } from "react";
import { useSelector, useDispatch } from 'react-redux';
import { setUserOb } from './features/authSlice';
import api from "./api/axios";



const App = () => {
  const dispatch = useDispatch();
  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
  const update = useSelector((state) => state.feed.update);
  const clickedProfile = useSelector((state) => state.auth.clickedProfile);
  const userOb = useSelector((state) => state.auth.userOb);

  useEffect(() => {
    if(isLoggedIn){

    api
        .get("/getprofile?user=" + userOb._id)
        .then((res) => {
            dispatch(setUserOb(res.data));
        })
        .catch((err) => {
            console.log("Error: ", err);
        });
      }
}
    , [update]);


  const handleLogOut = () => {
    dispatch(setUserOb(null));
    localStorage.removeItem("token");
  }

  return (

    <Router>
      <Navbar />
      <Routes>

        <Route
          path="/"
          element={
            isLoggedIn ?
              <Feed userID={userOb._id} userOb={userOb} />
              : <RegisterCheck route='login' />
          }
        />
        <Route path="/login" element={<RegisterCheck route='login' />} />
        <Route path="/myprofile" element={<Profile userProfile={userOb} self={true}  />} />
        <Route path="/profile" element={<Profile userProfile={clickedProfile}  self={false}/>} />

        <Route
          path="/register"
          element={<RegisterCheck route='register' />}
        />
      </Routes>
    </Router>
  );
};

export default App;
