import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import ListItem from '@mui/material/ListItem';
import { useEffect, useState } from 'react'
import { sendMessage } from '../api/socketManager'

const ChatUnit = ({ user, friend, handleChoos }) => {
    const [lastMessage, setLastMessage] = useState({
        userId: null,
        content: null,
        created_at: null
    })
    useEffect(() => {

        sendMessage('checkChats', { userId: user._id, friendId: friend._id }, (response) => {
            if (response.success) {
                const { userId, content, created_at } = response.data
                setLastMessage({ userId, content, created_at })
            }
        }
        )

    }, [])
    return (
        <ListItem key={friend._id} alignItems="flex-start">
            <ListItemAvatar>
                <Avatar alt={friend.name} src={friend.avatar} />
            </ListItemAvatar>
            <ListItemText
                primary={friend.name}
                secondary={
                    <>
                        <Typography
                            sx={{ display: 'inline' }}
                            component="span"
                            variant="body2"
                            color="text.primary"
                        >
                            {lastMessage.userId === null ? "" : lastMessage.userId.name}
                        </Typography>
                        {lastMessage.userId === null ? "Start your first chat with " + friend.name  : lastMessage.userId.content}
                    </>
                }
            />
        </ListItem>
    )
}
export default ChatUnit;
