import { createSlice } from '@reduxjs/toolkit'

const initialState = {
    currentChat: null,
    currentChatContent: {
        messages: []
    }
};

export const chatSlice = createSlice({
    name: 'chat',
    initialState,
    reducers: {
        setCurrentChat: (state, action) => {
            state.currentChat = action.payload;
        },
        setCurrentChatContent: (state, action) => {
            state.currentChatContent = action.payload;
        },
        addMessage: (state, action) => {
            state.currentChatContent.messages.push(action.payload);
        }
    },
});

// Action creators are generated for each case reducer function
export const { setCurrentChat, setCurrentChatContent, addMessage } = chatSlice.actions;

export default chatSlice.reducer;
