import Navbar from "./components/Navbar";
import Feed from "./components/Feed";
import RegisterCheck from "./components/RegisterCheck";
import Profile from "./components/Profile";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useEffect } from "react";
import { useSelector, useDispatch } from 'react-redux';
import { setUserOb, setLoggedIn } from './features/authSlice';
import api from "./api/axios";
import {useNavigate} from 'react-router-dom';
import Alert from "@mui/material/Alert";


const App = () => {
  const dispatch = useDispatch();
  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
  const update = useSelector((state) => state.feed.update);
  const clickedProfile = useSelector((state) => state.auth.clickedProfile);
  const userOb = useSelector((state) => state.auth.userOb);
  const navigate = useNavigate();
  const alertMsg = useSelector((state) => state.auth.alertMsg);


  useEffect(() => {
    if(isLoggedIn){

    api
        .get("/getprofile?user=" + userOb._id)
        .then((res) => {
            dispatch(setUserOb(res.data));
        })
        .catch((err) => {
            console.log("Error: ", err);
        });
      } else {
        navigate('/login')
      }
}
    , [update]);

    const displayAlert = () => {
      if (alertMsg === "") return "";
      if (!alertMsg.includes("successfully")) {

        return <Alert severity="error">{alertMsg}</Alert>;
      } else {
        return <Alert severity="success">{alertMsg}</Alert>;
      }
    };

    const removeFriend = (friendId) => {
      api
          .post("/removefriend", {
              userId: userOb._id,
              friendId: friendId,
          })
          .then((res) => {
              dispatch(setAlertMsg("Friend Removed"));
              dispatch(setUpdate());

          })
          .catch((err) => {
              console.log("Error: ", err);
          });
  }


  const handleLogOut = () => {
    dispatch(setUserOb(null));
    dispatch(setLoggedIn(false))

  }

  return (
    <div>
      <Navbar handleLogOut={handleLogOut} />
      {displayAlert()}
      <Routes>

        <Route
          path="/"
          element={
            isLoggedIn ?
              <Feed userID={userOb._id} userOb={userOb} removeFriend={removeFriend} />
              : <RegisterCheck route='login' />
          }
        />
        <Route path="/login" element={<RegisterCheck route='login' />} />
        <Route path="/myprofile" element={<Profile userProfile={userOb} self={true}  />} />
        <Route path="/profile" element={<Profile userProfile={clickedProfile} removeFriend={removeFriend} self={false}/>} />

        <Route
          path="/register"
          element={<RegisterCheck route='register' />}
        />
      </Routes>
      </div>
  );
};

export default App;
