import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import { useSelector } from 'react-redux'
import ChatMessage from './ChatMessage'
import ChatSendMessage from './ChatSendMessage'
import { useRef, useEffect } from 'react'
import { addMessage } from '../features/chatSlice';
import { registerMessageHandler, unregisterMessageHandler } from '../api/socketManager';
import { useDispatch } from 'react-redux'

export default function ChatBox() {
    const dispatch = useDispatch();
    const messagesEndRef = useRef(null);
    const currentChat = useSelector((state) => state.chat.currentChat);
    const currentChatContent = useSelector((state) => state.chat.currentChatContent);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };


    useEffect(() => {
        const handleMessage = (data) => {
            console.log(data.data);
            if (data.chatId === currentChat)
                dispatch(addMessage(data.data));

        };
        registerMessageHandler('chatMsg', handleMessage);

        return () => {
            unregisterMessageHandler('chatMsg');
        }

    },);

    useEffect(() => {

        scrollToBottom()
    }, [currentChatContent]);

    return (

        <div style={{ position: 'relative', maxHeight: '700px' }}>

            <div className='shadow-5' style={{ maxHeight: '650px', overflowY: 'auto', scrollbarWidth: 'none', msOverflowStyle: 'none' }}>
                <List sx={{ width: '100%', maxWidth: '360px', bgcolor: 'background.paper' }} ref={messagesEndRef}>
                    {currentChat ? currentChatContent?.map((message, index) => (
                        <ChatMessage key={message._id} message={message} />
                    )) : null}
                </List>
                <style>
                    {`
            ::-webkit-scrollbar {
              display: none;
            }
            `}
                </style>
            </div >

            <div style={{ position: 'absolute', bottom: '0', width: '100%' }}>
                <ChatSendMessage />
            </div>
        </div>

    );

}
