import React from 'react';
import { useEffect } from 'react';
import {
    MDBRow,
    MDBCol,
    MDBInput,
    MDBCheckbox,
    MDBBtn,
    MDBIcon,
    MDBTabs,
    MDBTabsItem,
    MDBTabsLink,
    MDBTabsContent,
    MDBTabsPane
} from 'mdb-react-ui-kit';
import BirthdayPicker from './BirthdayPicker';
import { useState } from "react";
import Alert from "@mui/material/Alert";
import { useDispatch, useSelector } from 'react-redux';
import { setEmail, setPassword, setRePassword, setName, setDate, setPhone, setAddress, setAlertMsg } from './features/registerSlice';


const handleSubmit = (name, email, password, rePassword, address, phone, date, setAlertMsg) => {
    if (name === " ") {
      dispatch(setAlertMsg("Please fill in your name"));
      return;
    } else if (email === " ") {
      setAlertMsg("Please fill in your email address");
      return;
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      setAlertMsg("Please enter a valid email address");
      return;

    } else if (password === " ") {
      setAlertMsg("Please fill in your password");
      return;
    } else {
      if (name === null) {
        handleLogin(email, password, setAlertMsg);
      } else {
        handleRegister(email, password, rePassword, name, address, phone, date, setAlertMsg);
      }
    }
  };


export default function RegisterCheck({ handleSubmit, route }) {
    const dispatch = useDispatch();
    const [loginRegisterActive, setLoginRegisterActive] = useState('register');

    const email = useSelector((state) => state.register.email);
    const password = useSelector((state) => state.register.password);
    const rePassword = useSelector((state) => state.register.rePassword);
    const name = useSelector((state) => state.register.name);
    const date = useSelector((state) => state.register.date);
    const phone = useSelector((state) => state.register.phone);
    const address = useSelector((state) => state.register.address);
    const alertMsg = useSelector((state) => state.register.alertMsg);


    useEffect(() => {
        if (route === 'login') setLoginRegisterActive('login');
        else setLoginRegisterActive('register');
    }, []);

    const displayAlert = () => {
        if (alertMsg === "") return "";
        if (!alertMsg.includes("successfully")) {

            return <Alert severity="error">{alertMsg}</Alert>;
        } else {
            return <Alert severity="success">{alertMsg}</Alert>;
        }
    };

    return (
        <div className='ml7 mr7 mt3 shadow-4'>
            {displayAlert()}
            <div className='pa4'>
                <MDBTabs pills justify className='mb-3'>
                    <MDBTabsItem>
                        <MDBTabsLink
                            onClick={() => setLoginRegisterActive('login')}
                            active={loginRegisterActive === 'login'}
                        >
                            Login
                        </MDBTabsLink>
                    </MDBTabsItem>
                    <MDBTabsItem>
                        <MDBTabsLink
                            onClick={() => setLoginRegisterActive('register')}
                            active={loginRegisterActive === 'register'}
                        >
                            Register
                        </MDBTabsLink>
                    </MDBTabsItem>
                </MDBTabs>

                <MDBTabsContent>
                    <MDBTabsPane show={loginRegisterActive === 'login'}>
                        <form>
                            <div className='text-center mb-3'>
                                <p>Sign up with:</p>

                                <MDBBtn floating color="secondary" className='mx-1'>
                                    <MDBIcon fab icon='facebook-f' />
                                </MDBBtn>

                                <MDBBtn floating color="secondary" className='mx-1'>
                                    <MDBIcon fab icon='google' />
                                </MDBBtn>

                                <MDBBtn floating color="secondary" className='mx-1'>
                                    <MDBIcon fab icon='twitter' />
                                </MDBBtn>

                                <MDBBtn floating color="secondary" className='mx-1'>
                                    <MDBIcon fab icon='github' />
                                </MDBBtn>
                            </div>

                            <p className='text-center'>or:</p>

                            <MDBInput className='mb-4' type='email' id='form8Example3' label='Email address' onChange={(e) => { dispatch(setLoginEmail(e.target.value)) }} />
                            <MDBInput className='mb-4' type='password' id='form8Example4' label='Password' onChange={(e) => { dispatch(setLoginPassword(e.target.value)) }} />

                            <MDBRow className='mb-4'>
                                <MDBCol className='d-flex justify-content-center'>
                                </MDBCol>
                            </MDBRow>

                            <MDBBtn type='submit' className='mb-4' block onClick={(e) => {
                                handleSubmit();
                                e.preventDefault()
                            }}>
                                Sign in
                            </MDBBtn>

                            <div className='text-center'>
                                <p>
                                    Not a member? <a href='/register' >Register</a>
                                </p>
                            </div>
                        </form>
                    </MDBTabsPane>
                    <MDBTabsPane show={loginRegisterActive === 'register'}>
                        <form>
                            <div className='text-center mb-3'>
                                <p>Sign up with:</p>

                                <MDBBtn floating color="secondary" className='mx-1'>
                                    <MDBIcon fab icon='facebook-f' />
                                </MDBBtn>

                                <MDBBtn floating color="secondary" className='mx-1'>
                                    <MDBIcon fab icon='google' />
                                </MDBBtn>

                                <MDBBtn floating color="secondary" className='mx-1'>
                                    <MDBIcon fab icon='twitter' />
                                </MDBBtn>

                                <MDBBtn floating color="secondary" className='mx-1'>
                                    <MDBIcon fab icon='github' />
                                </MDBBtn>
                            </div>

                            <p className='text-center'>or:</p>

                            <MDBInput className='mb-4' id='form8Example1' label='Full Name' onChange={(e) => { dispatch(setName(e.target.value)) }} />
                            <MDBInput className='mb-4' type='email' id='form8Example3' label='Email address' onChange={(e) => { dispatch(setEmail(e.target.value)) }} />
                            <MDBInput className='mb-4' type='password' id='form8Example4' label='Password' onChange={(e) => { dispatch(setPassword(e.target.value)) }} />
                            <MDBInput className='mb-4' type='password' id='form8Example5' label='Repeat password' onChange={(e) => { dispatch(setRePassword(e.target.value)) }} />
                            <MDBInput className='mb-4' id='form8Example1' label='Address' onChange={(e) => { dispatch(setAddress(e.target.value)) }} />
                            <MDBInput className='mb-4' id='form8Example1' type='number' label='Phone' onChange={(e) => { dispatch(setPhone(e.target.value)) }} />
                            <BirthdayPicker date={date} setDate={setDate} />

                            <MDBCheckbox
                                wrapperClass='d-flex justify-content-center mb-4'
                                id='form8Example6'
                                label='I have read and agree to the terms'
                                defaultChecked
                            />

                            <MDBBtn type='submit' className='mb-4' block onClick={(e) => {
                                handleSubmit();
                                e.preventDefault()
                            }

                            }>
                                Sign up
                            </MDBBtn>
                            <div className='text-center'>
                                <p>
                                    Already have an account? <a href='/login' >Sign in</a>
                                </p>
                            </div>
                        </form>
                    </MDBTabsPane>
                </MDBTabsContent>
            </div>
        </div >
    );
}
