import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import { useSelector } from 'react-redux'
import ChatMessage from './ChatMessage'
import ChatSendMessage from './ChatSendMessage'
import { useRef, useEffect } from 'react'

export default function ChatBox() {
    const messagesEndRef = useRef(null);
    const currentChat = useSelector((state) => state.chat.currentChat);
    const currentChatContent = useSelector((state) => state.chat.currentChatContent);
   
    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom()
    }, [currentChatContent]);

    return (
        <div>
            <div className='shadow-5' style={{ maxHeight: 700,height:700, overflowY: 'auto', scrollbarWidth: 'none', msOverflowStyle: 'none' }}>
                <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper', height: '100%' }}>
                    {currentChatContent.messages !== null ? currentChatContent?.messages.map((message, index) => {
                        return <ChatMessage key={message._id} message={message} />
                    }) : null}
                </List>
                <div ref={messagesEndRef} />
                <style>
                    {`
                ::-webkit-scrollbar {
                    display: none;
                }
                `}
                </style>
            </div >
            <ChatSendMessage />
        </div>
    );

}
