import * as React from 'react';
import Box from '@mui/material/Box';
import {TextField } from '@mui/material';


export default function PostText({setText, placeholder}) {
  const classes = useStyles();

  return (
    <Box
      component="form"
      sx={{
   
        display: 'flex',
        flex: 1, 
        '& .MuiTextField-root': { 
          m: 0, 
          width: '100%', 
        },
      }}
      noValidate
      autoComplete="off"
    >
    
        <TextField
          
          id="outlined-multiline-flexible"
          label={placeholder}
          onChange={(event)=>setText(event.target.value)}
          multiline
          size="small"
          maxRows={3}
          />
    </Box>
  );
}
