import * as React from 'react';

import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardContent from '@mui/material/CardContent';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
import Menu from '@mui/material/Menu';
import api from "../api/axios";
import MenuItem from '@mui/material/MenuItem';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

export default function Comment({ postId, comment, userID, compareDates, setUpdate, update }) {
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };
    const DeleteComment = () => {
        api
            .post("/deletecomment", {
                postId: postId,
                commentId: comment._id,
                userId: userID,
            })
            .then((res) => {
                setUpdate(!update);
                console.log("Deleted")
            })
            .catch((err) => {
                console.log("Error: ", err);
            });
    }
    const likeComment = () => {
        api.post("/like", { user: userId, post: post._id })
            .then((res) => {
                setUpdate(!update);
            })
            .catch((err) => {
                console.log("Error: ", err);
            });
    }
    const unlike = () => {
        api.post("/unlike", { user: userId, post: post._id })
            .then((res) => {
                setUpdate(!update);

            })
            .catch((err) => {
                console.log("Error: ", err);
            });
    }

    const { userId, content, created_at, likes } = comment;
    const username = userId.name;
    let dates = created_at.toString().substring(0, 10);
    let times = created_at.toString().substring(11, 16);

    return (
        <div className="pa2">
            <Card sx={{ maxWidth: 'auto' }}>
                <CardHeader
                    avatar={
                        <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
                            R
                        </Avatar>
                    }
                    action={

                        userID === userId._id ? (
                            <div>
                                <IconButton aria-label="settings" onClick={handleClick}  >
                                    <MoreVertIcon />
                                </IconButton>
                                <Menu
                                    id="simple-menu"
                                    anchorEl={anchorEl}
                                    open={Boolean(anchorEl)}
                                    onClose={handleClose}
                                >
                                    <MenuItem onClick={handleClose}><EditIcon style={{ color: 'blue' }} />  Edit</MenuItem>
                                    <MenuItem onClick={() => {
                                        DeleteComment();
                                        handleClose();
                                    }}><DeleteIcon style={{ color: 'red' }} />Delete</MenuItem>
                                </Menu>
                            </div>
                        ) : null
                    }
                    title={username}
                    subheader={compareDates(created_at) ? times : dates + " " + times}
                />
                <CardContent>
                    <Typography variant="body2" color="text.secondary">
                        {content}
                    </Typography>
                </CardContent>
                <IconButton aria-label="like">
                    <StyledBadge badgeContent={likes.length} color="secondary">
                        {likes.includes(userId) ?
                            <FavoriteIcon style={{ color: 'red' }} onClick={() => unLikeComment()} />
                            :
                            <FavoriteBorderIcon onClick={() => likeComment()} />}
                    </StyledBadge>
                </IconButton>
            </Card>
        </div>
    );
}
