// import "./App.css";
import Navbar from "./components/Navbar";
import Feed from "./components/Feed";
import Register from "./components/Register";
import Login from "./components/Login";
import RegisterCheck from "./components/RegisterCheck";
import Profile from "./components/Profile";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useState } from "react";
import api from "./api/axios";
import { useNavigate } from "react-router-dom";


const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [clickedProfile, setClickedProfile] = useState();
  const [userID, setUserID] = useState("6509b342c87b801de5e49976");
  const [userOb, setUserOb] = useState();
  const navigate = useNavigate();

  const handleSubmit = (name, email, password, rePassword, address, phone, date, setAlertMsg) => {
    console.log(name)
    if (name === " ") {
      setAlertMsg("Please fill in your name");
      return;
    } else if (email === " ") {
      setAlertMsg("Please fill in your email address");
      return;
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      setAlertMsg("Please enter a valid email address");
      return;

    } else if (password === " ") {
      setAlertMsg("Please fill in your password");
      return;
    } else {
      if (name === null) {
        handleLogin(email, password, setAlertMsg);
      } else {
        handleRegister(email, password, rePassword, name, address, phone, date, setAlertMsg);
      }
    }
  };

  const handlePost = (text) => {
    api
      .post("/post", {
        user: userID,
        description: text,
      })
      .catch((err) => {
        console.log("Error: ", err);
      });
  }

  const handleLogin = (email, password, setAlertMsg) => {
    api
      .post("/login", {
        email: email,
        password: password,
      })
      .then((res) => {
        setAlertMsg("You have logged in successfully!");
        setIsLoggedIn(true);
        setUserID(res.data.id);
        setUserOb(res.data.userOb)

      })
      .catch((err) => {
        setAlertMsg("Wrong email or password, please try again!");
      });
  };
  const handleRegister = (email, password, rePassword, name, address, phone, date, setAlertMsg) => {
    console.log(phone)
    if (password !== rePassword) {
      setAlertMsg("Passwords do not match!");
      return;
    }
    if (password.length < 8) {
      setAlertMsg("Password must be at least 8 characters long!");
      return;
    }

    if (phone === undefined) {
      setAlertMsg("Please fill in your phone number!");
      return;
    }
    if (address === "") {
      setAlertMsg("Please fill in your address!");
      return;
    }

    if (date === undefined) {
      setAlertMsg("Please fill in your date of birth!");
      return;
    }
    if (date > Date.now()) {
      setAlertMsg("Please fill in a valid date of birth!");
      return;
    }
    api
      .post("/register", {
        email: email,
        password: password,
        name: name,
        address: address,
        phone: phone,
        birthday: new Date(date),

      })
      .then((res) => {
        setAlertMsg("You have signed up to our service successfully!");
      })
      .catch((err) => {
        setAlertMsg("This email address is already in use!");
      });
  };


  return (
    <Router>
      <Navbar />
      <Routes>
        <Route
          path="/"
          element={
            isLoggedIn ?
              <Feed userID={userID} setClickedProfile={setClickedProfile} handlePost={handlePost} />
              : <Login handleSubmit={handleSubmit} />
          }
        />
        <Route path="/login" element={<RegisterCheck route='login' handleSubmit={handleSubmit} />} />
        <Route path="/myprofile" element={<Profile user={userOb} />} />
        <Route path="/profile" element={<Profile user={clickedProfile} />} />

        <Route
          path="/register"
          element={<RegisterCheck route='register' handleSubmit={handleSubmit} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
