import { useEffect, useState } from "react";
import Post from "./Post";
import PostCreate from "./PostCreate";
import Stack from '@mui/material/Stack';
import api from "../api/axios";
import { Grid } from '@mui/material';
import Friends from "./Friends";
import { useNavigate } from "react-router-dom";
import { useSelector } from 'react-redux';
import { setClickedProfile, userOb } from '../features/authSlice';
import {setUpdate, setPosts} from '../features/feedSlice';



const Feed = ({ handlePost, handleComment, setClickedProfile }) => {
    const [posts, setPosts] = useState([]);
    const [update, setUpdate] = useState(false);
    const navigate = useNavigate();
    
    const userOb = useSelector((state) => state.auth.userOb);
    const userID = userOb._id;

    useEffect(() => {
        api
        .get("/feed?user=" + userID)
        .then((res) => {
            setPosts(res.data);
        })
        .catch((err) => {
            console.log("Error: ", err);
        });
    }
        , [update]);

    const { friendPosts, myPosts } = (posts === undefined || null) ? { friendPosts: [], myPosts: [] } : posts;
    const totalPosts = friendPosts?.concat(myPosts);

    const findProfile = (user) => {
      console.log(user)
      api.get("/getprofile", {
          params: {
              user: user,
          },
      }).then((res) => {
        console.log(res.data)
          setClickedProfile(res.data[0])
          user===userID ? navigate("/myprofile") : navigate("/profile")
      })
  }

    return (
<div style={{ position: 'relative', marginTop: '20px', width: '100%', display: 'flex', justifyContent: 'center' }}>

{/* Friends Component on the Far Left */}
<div style={{ position: 'absolute', left: 10 }}>
    <Friends friendslist={userOb.friends} setUpdate={setUpdate} update={update}  userID={userID} />
</div>

{/* Centered Container for Posts */}
<div style={{ width: '60%' }}>
    
    {/* PostCreate component */}
    <PostCreate handlePost={handlePost} setUpdate={setUpdate} update={update} style={{ width: '100%', maxWidth: '750px', margin: '0 auto'}} />

    {/* Posts */}
    <Grid container direction="column" spacing={2} sx={{marginTop:'10px'}}>
        {totalPosts?.map(post => (
            <Grid item key={post._id} xs={12}>
                <Post key={post._id} findProfile={findProfile} handleComment={handleComment} setUpdate={setUpdate} setClickedProfile={setClickedProfile} update={update} post={post} userId={userID} style={{ width: '100%', maxWidth: '750px', margin: '0 auto' }} />
            </Grid>
        ))}
    </Grid>

</div>

</div>

      );
    };
    

export default Feed;
