import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import ListItem from '@mui/material/ListItem';
import { useEffect, useState, useRef } from 'react'
import { sendMessage } from '../api/socketManager'
import { setCurrentChat, setCurrentChatContent } from '../features/chatSlice';
import { useDispatch, useSelector } from 'react-redux'


const ChatUnit = ({ user, friend }) => {
    const dispatch = useDispatch()
    const currentChatContent = useSelector((state) => state.chat.currentChatContent);
    const [userHasInteracted, setUserHasInteracted] = useState(false);
    const [chatContent, setChatContent] = useState([])
    const [chatId, setChatId] = useState(null)
    const [chatUpdate, setChatUpdate] = useState(false)
    const [dataFetched, setDataFetched] = useState(false);
    const [lastMessage, setLastMessage] = useState({
        userId: null,
        content: null,
        created_at: null
    })


    useEffect(() => {
        if (userHasInteracted) {
            sendMessage('checkChats', { userId: user._id, friendId: friend._id }, (response) => {
                if (response.success) {
                    setChatId(response.data._id);
                    if (response.data.messages.length >= 1) {
                        const { userId, content, created_at } = response.data.messages[response.data.messages.length - 1];
                        setChatContent(response.data.messages);
                        setLastMessage({ userId, content, created_at });
                    }
                    setDataFetched(true); // Only set this to true after message has been sent and data processed
                }
            });
        }
    }, [chatUpdate, userHasInteracted]);

    useEffect(() => {
        if (dataFetched) {
            dispatch(setCurrentChat(chatId));
            dispatch(setCurrentChatContent(chatContent));
            setDataFetched(false);
        }
    }, [dataFetched, chatId, chatContent, dispatch]);
    const handleClick = () => {
        setUserHasInteracted(true);
        setChatUpdate(prev => !prev);
    };

    return (

        dataFetched ? (
            <ListItem key={friend._id} alignItems="flex-start" onClick={() => handleClick()} >
                <ListItemAvatar>
                    <Avatar alt={friend.name} src={friend.avatar} />
                </ListItemAvatar>
                <ListItemText
                    primary={friend.name}
                    secondary={
                        <>
                            <Typography
                                sx={{ display: 'inline' }}
                                component="span"
                                variant="body2"
                                color="text.primary"
                            >
                                {lastMessage.userId === null ? "" : lastMessage.userId.name}
                            </Typography>
                            {lastMessage.userId === null ? "Start your first chat with " + friend.name : " : " + lastMessage.content}
                        </>
                    }
                />
            </ListItem >
        ) : null

    )
}
export default ChatUnit;
