import { createSlice } from '@reduxjs/toolkit'


const initialState = {
loginEmail:null,
loginPassword:null,
loginAlertMsg:null,
};

export const loginSlice = createSlice({
    name: 'login',
    initialState,
    reducers: {
        setLoginEmail: (state, action) => {
            state.loginEmail = action.payload;
        },
        setLoginPassword: (state, action) => {
            state.loginPassword = action.payload;
        },
        setLoginAlertMsg: (state, action) => {
            state.loginAlertMsg = action.payload;
        },
    },
})


export const {setLoginEmail, setLoginPassword, setLoginAlertMsg} = loginSlice.actions

export default loginSlice.reducer
