import io from 'socket.io-client';

let socket;

export const initializeSocket = (userId) => {
  if (!socket) {
    socket = io('http://localhost:3001', {
      query: {
        userId: userId,
      },
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
    });
  }
}


export const disconnectSocket = () => {
  if (socket) {
    socket.disconnect();
    socket = null;
  }
}

export const sendMessage = (messageType, data) => {
  if (!socket) return;
  socket.emit(messageType, data);
}

export const registerMessageHandler = (messageType, handler) => {

  if (!socket) console.log("ERROR: no socket connection");

  socket.on(messageType, handler);
}

export const unregisterMessageHandler = (messageType) => {
  if (!socket) return;
  socket.off(messageType);
}
