import { useEffect } from 'react'
import * as React from 'react';
import { useSelector } from 'react-redux'
import List from '@mui/material/List';
import ChatUnit from './ChatUnit'
import Divider from '@mui/material/Divider';

import { registerMessageHandler, unregisterMessageHandler } from '../api/socketManager';
import ListSubheader from '@mui/material/ListSubheader';

export default function FriendsChats() {

    const user = useSelector((state) => state.auth.userOb);
    const friends = user.friends;
    return (

        <List className="shadow-5" sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper', borderRadius: '15px', maxHeight: 700, height: 700, overflowY: 'auto', scrollbarWidth: 'none', msOverflowStyle: 'none' }} subheader={<ListSubheader>Friends</ListSubheader>}>
            {!friends ? null : friends.map((friend) => {
                return <ChatUnit friend={friend} user={user} />
            }
            )}

            <Divider variant="inset" component="li" />
        </List>
    );
}
