import React from 'react';
import {
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBCard,
  MDBCardText,
  MDBCardBody,
  MDBCardImage,
  MDBBtn,
  MDBBreadcrumb,
  MDBBreadcrumbItem,
  MDBProgress,
  MDBProgressBar,
  MDBIcon,
  MDBListGroup,
  MDBListGroupItem
} from 'mdb-react-ui-kit';
import { useRef, useState } from 'react'
import { useParams } from 'react-router-dom';
import { sendMessage } from '../api/socketManager';
import api from '../api/axios'
import { setUpdate, setClickedProfile } from '../features/feedSlice';

import { useSelector, useDispatch } from 'react-redux';

export default function Profile({ self, removeFriend, changeAlert, handleFriendRequest }) {
  const [sent, setSent] = useState(false);
  const { userId } = useParams();
  const dispatch = useDispatch();
  const inputRef = useRef(null)
  const user = useSelector((state) => state.auth.userOb);
  let userProfile = useSelector((state) => state.feed.clickedProfile);
  userProfile = self ? user : userProfile
  const [avatar, setAvatar] = useState(userProfile.avatar)
  const [profile, setProfile] = useState(null);
  const [isSelf, setIsSelf] = useState(false);
  const [error, setError] = useState(null);

  const { name, email, phone, address, birthday } = userProfile
  const date = new Date(birthday).toLocaleDateString()


  useEffect(() => {
    if (userId === user._id) {
      setIsSelf(true);
      setProfile(user);
    } else {
      setIsSelf(false);
      api.get("/getprofile", { params: { user: userId } })
        .then((res) => {
          setProfile(res.data);
        })
        .catch((err) => {
          changeAlert
          setError('Profile not found or other error message.');
        });
    }
  }, [userId, currentUser]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!profile) {
    return <div>Loading...</div>;
  }

  const handleAddFriend = () => {
    sendMessage('addFriend', { userId: user._id, friendId: userProfile._id }, (response) => {
      console.log("sdsd")
      if (response.success) {
        changeAlert('Friend request sent to ' + userProfile.name + " successfully!");
        setSent(true);
        dispatch(setUpdate());
      } else {
        changeAlert("There was a problem sending friend request to " + userProfile.name)
      }
    });

    // api.post('/addfriend', { id: user._id, friendId: userProfile._id }).then((res) => {
    //   changeAlert('Friend request sent to ' + userProfile.name + " successfully!");
    //   setSent(true);
    //   dispatch(setUpdate());
  }



  const handleUpload = () => {
    inputRef.current.click();
  }

  const handleFileChange = async event => {
    const fileObj = event.target.files && event.target.files[0];
    if (!fileObj) {
      return;
    }
    event.target.value = null;
    const base64 = await convertToBase64(fileObj)
    setAvatar(base64)
    api.post('/uploadpic', { base64: base64, id: user._id }).then((res) => {
      dispatch(setUpdate());
    })

  }


  const convertToBase64 = (file) => {

    return new Promise((resolve, reject) => {
      const fileReader = new FileReader();
      fileReader.readAsDataURL(file);
      fileReader.onload = () => {
        resolve(fileReader.result);

      };
      fileReader.onerror = (error) => {

        reject(error);
      };
    })

  }

  const checkIfFriends = () => {
    const friends = user.friends.map(friend => {
      return friend._id
    })

    if (friends.includes(userProfile._id)) {
      return (
        <>
          <MDBBtn onClick={() => removeFriend(userProfile._id)}>Remove Friend</MDBBtn>
          <MDBBtn outline className="ms-1">Message</MDBBtn>
        </>
      )
    } else {
      const myRequests = user.receivedfriendRequests;
      const mySentRequests = user.sentfriendRequests;

      if (mySentRequests?.find(req => req.userId == userProfile._id))
        return (
          <MDBBtn onClick={() => changeAlert("You have already sent friend request to " + userProfile.name)}>Pending</MDBBtn>
        )
      if (myRequests?.find(req => req.userId == userProfile._id))
        return (
          <>
            <MDBBtn onClick={() => handleFriendRequest(userProfile._id, null, true)}>Accept</MDBBtn>
            <MDBBtn outline className="ms-1" onClick={() => handleFriendRequest(userProfile._id, null, false)}>Deny</MDBBtn>
          </>
        )
      return (
        <MDBBtn onClick={() => handleAddFriend()}>Add Friend</MDBBtn>
      )
    }
  }
  return (
    <section style={{ backgroundColor: '#eee' }}>
      <MDBContainer className="py-5">
        <MDBRow>
          <MDBCol>
            <MDBBreadcrumb className="bg-light rounded-3 p-3 mb-4">
              <MDBBreadcrumbItem active>User Profile</MDBBreadcrumbItem>
            </MDBBreadcrumb>
          </MDBCol>
        </MDBRow>

        <MDBRow>
          <MDBCol lg="4">
            <MDBCard className="mb-4">
              <MDBCardBody className="text-center">
                <MDBCardImage
                  src={avatar}
                  alt="avatar"
                  className="rounded-circle"
                  style={{ width: '150px' }}
                  fluid />

                <div className="d-flex justify-content-center mb-2 mt3">
                  {user._id !== userProfile._id ? (
                    <>
                      {checkIfFriends()}
                    </>
                  ) : (
                    <>
                      <input
                        style={{ display: 'none' }}
                        ref={inputRef}
                        type="file"
                        accept=".png, .jpg, .jpeg"
                        onChange={handleFileChange}
                      />
                      <MDBBtn outline className="ms-1" onClick={() => { handleUpload() }}>Upload Avatar</MDBBtn>
                    </>
                  )}
                </div>
              </MDBCardBody>
            </MDBCard>

            <MDBCard className="mb-4 mb-lg-0">
              <MDBCardBody className="p-0">
                <MDBListGroup flush className="rounded-3">
                  <MDBListGroupItem className="d-flex justify-content-between align-items-center p-3">
                    <MDBIcon fas icon="globe fa-lg text-warning" />
                    <MDBCardText>https://mdbootstrap.com</MDBCardText>
                  </MDBListGroupItem>
                  <MDBListGroupItem className="d-flex justify-content-between align-items-center p-3">
                    <MDBIcon fab icon="github fa-lg" style={{ color: '#333333' }} />
                    <MDBCardText>mdbootstrap</MDBCardText>
                  </MDBListGroupItem>
                  <MDBListGroupItem className="d-flex justify-content-between align-items-center p-3">
                    <MDBIcon fab icon="twitter fa-lg" style={{ color: '#55acee' }} />
                    <MDBCardText>@mdbootstrap</MDBCardText>
                  </MDBListGroupItem>
                  <MDBListGroupItem className="d-flex justify-content-between align-items-center p-3">
                    <MDBIcon fab icon="instagram fa-lg" style={{ color: '#ac2bac' }} />
                    <MDBCardText>mdbootstrap</MDBCardText>
                  </MDBListGroupItem>
                  <MDBListGroupItem className="d-flex justify-content-between align-items-center p-3">
                    <MDBIcon fab icon="facebook fa-lg" style={{ color: '#3b5998' }} />
                    <MDBCardText>mdbootstrap</MDBCardText>
                  </MDBListGroupItem>
                </MDBListGroup>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
          <MDBCol lg="8">
            <MDBCard className="mb-4">
              <MDBCardBody>
                <MDBRow>
                  <MDBCol sm="3">
                    <MDBCardText>Full Name</MDBCardText>
                  </MDBCol>
                  <MDBCol sm="9">
                    <MDBCardText className="text-muted">{name}</MDBCardText>
                  </MDBCol>
                </MDBRow>
                <hr />
                <MDBRow>
                  <MDBCol sm="3">
                    <MDBCardText>Email</MDBCardText>
                  </MDBCol>
                  <MDBCol sm="9">
                    <MDBCardText className="text-muted">{email}</MDBCardText>
                  </MDBCol>
                </MDBRow>
                <hr />
                <MDBRow>
                  <MDBCol sm="3">
                    <MDBCardText>Phone</MDBCardText>
                  </MDBCol>
                  <MDBCol sm="9">
                    <MDBCardText className="text-muted">{phone}</MDBCardText>
                  </MDBCol>
                </MDBRow>
                <hr />
                <MDBRow>
                  <MDBCol sm="3">
                    <MDBCardText>Birthday</MDBCardText>
                  </MDBCol>
                  <MDBCol sm="9">
                    <MDBCardText className="text-muted">{date}</MDBCardText>
                  </MDBCol>
                </MDBRow>
                <hr />
                <MDBRow>
                  <MDBCol sm="3">
                    <MDBCardText>Address</MDBCardText>
                  </MDBCol>
                  <MDBCol sm="9">
                    <MDBCardText className="text-muted">{address}</MDBCardText>
                  </MDBCol>
                </MDBRow>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}
