import { useEffect, useSelector } from 'react-redux'
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import { registerMessageHandler, unregisterMessageHandler } from '../api/socketManager';


export default function FriendsChats() {

    const user = useSelector((state) => state.auth.userOb);


    return (
        <List className="shadow-5" sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
            {user.friends.map((friend) => {
                <ListItem alignItems="flex-start">
                    <ListItemAvatar>
                        <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
                    </ListItemAvatar>
                    <ListItemText
                        primary="Brunch this weekend?"
                        secondary={
                            <>
                                <Typography
                                    sx={{ display: 'inline' }}
                                    component="span"
                                    variant="body2"
                                    color="text.primary"
                                >
                                    Ali Connors
                                </Typography>
                                {" — I'll be in your neighborhood doing errands this…"}
                            </>
                        }
                    />
                </ListItem>

            }
            )}

            <Divider variant="inset" component="li" />
        </List>
    );
}
