// import "./App.css";
import Navbar from "./components/Navbar";
import Feed from "./components/Feed";
import RegisterCheck from "./components/RegisterCheck";
import Profile from "./components/Profile";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useState } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { setLoggedIn, setUserOb, setClickedProfile } from './features/authSlice';
import {setAlertMsg} from './features/registerSlice';

import api from "./api/axios";



const App = () => {
  const dispatch = useDispatch();
  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
  const clickedProfile = useSelector((state) => state.auth.clickedProfile);
  const userOb = useSelector((state) => state.auth.userOb);

  console.log(isLoggedIn)



  const handlePost = (text) => {
    api
      .post("/post", {
        user: userOb._id,
        description: text,
      })
      .catch((err) => {
        console.log("Error: ", err);
      });
  }




  return (
    <Router>
      <Navbar />
      <Routes>
        <Route
          path="/"
          element={
            isLoggedIn ?
              <Feed userID={userOb._id} userOb={userOb} setClickedProfile={setClickedProfile} handlePost={handlePost} />
              : <RegisterCheck route='login'  />
          }
        />
        <Route path="/login" element={<RegisterCheck route='login' handleSubmit={handleSubmit} />} />
        <Route path="/myprofile" element={<Profile user={userOb} userProfile={userOb} self={true} />} />
        <Route path="/profile" element={<Profile user={userOb} userProfile={clickedProfile} self={false} />} />

        <Route
          path="/register"
          element={<RegisterCheck route='register' handleSubmit={handleSubmit} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
