// import "./App.css";
import Navbar from "./components/Navbar";
import Feed from "./components/Feed";
import RegisterCheck from "./components/RegisterCheck";
import Profile from "./components/Profile";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useState } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { setEmail, setPassword, setRePassword, setName, setDate, setPhone, setAddress } from './features/authSlice';

import api from "./api/axios";



const App = () => {
  const dispatch = useDispatch();
  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
  const clickedProfile = useSelector((state) => state.auth.clickedProfile);
  const userOb = useSelector((state) => state.auth.userOb);
  console.log(isLoggedIn)

  const handleSubmit = (name, email, password, rePassword, address, phone, date, setAlertMsg) => {
    if (name === " ") {
      setAlertMsg("Please fill in your name");
      return;
    } else if (email === " ") {
      setAlertMsg("Please fill in your email address");
      return;
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      setAlertMsg("Please enter a valid email address");
      return;

    } else if (password === " ") {
      setAlertMsg("Please fill in your password");
      return;
    } else {
      if (name === null) {
        handleLogin(email, password, setAlertMsg);
      } else {
        handleRegister(email, password, rePassword, name, address, phone, date, setAlertMsg);
      }
    }
  };

  const handlePost = (text) => {
    api
      .post("/post", {
        user: userOb._id,
        description: text,
      })
      .catch((err) => {
        console.log("Error: ", err);
      });
  }

  const handleLogin = (email, password, setAlertMsg) => {
    api
      .post("/login", {
        email: email,
        password: password,
      })
      .then((res) => {
        setAlertMsg("You have logged in successfully!");
        dispatch(setLoggedIn(true));
        dispatch(setUserOb(res.data.userOb));

      })
      .catch((err) => {
        setAlertMsg("Wrong email or password, please try again!");
      });
  };
  const handleRegister = (email, password, rePassword, name, address, phone, date, setAlertMsg) => {
    if (password !== rePassword) {
      setAlertMsg("Passwords do not match!");
      return;
    }
    if (password.length < 8) {
      setAlertMsg("Password must be at least 8 characters long!");
      return;
    }

    if (phone === undefined) {
      setAlertMsg("Please fill in your phone number!");
      return;
    }
    if (address === "") {
      setAlertMsg("Please fill in your address!");
      return;
    }

    if (date === undefined) {
      setAlertMsg("Please fill in your date of birth!");
      return;
    }
    if (date > Date.now()) {
      setAlertMsg("Please fill in a valid date of birth!");
      return;
    }
    api
      .post("/register", {
        email: email,
        password: password,
        name: name,
        address: address,
        phone: phone,
        birthday: new Date(date),

      })
      .then((res) => {
        setAlertMsg("You have signed up to our service successfully!");
      })
      .catch((err) => {
        setAlertMsg("This email address is already in use!");
      });
  };


  return (
    <Router>
      <Navbar />
      <Routes>
        <Route
          path="/"
          element={
            isLoggedIn ?
              <Feed userID={userOb._id} userOb={userOb} setClickedProfile={setClickedProfile} handlePost={handlePost} />
              : <RegisterCheck route='login' handleSubmit={handleSubmit}  />
          }
        />
        <Route path="/login" element={<RegisterCheck route='login' handleSubmit={handleSubmit} />} />
        <Route path="/myprofile" element={<Profile user={userOb} userProfile={userOb} self={true} />} />
        <Route path="/profile" element={<Profile user={userOb} userProfile={clickedProfile} self={false} />} />

        <Route
          path="/register"
          element={<RegisterCheck route='register' handleSubmit={handleSubmit} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
